$('input[type=file]').each(function() { 
    $(this).attr('accept', '.pdf'); 
});

var dateCreated = scope.findFormFieldWithId(scope.allFormFields, 'datecreated');
dateCreated.value = new Date(dateCreated.value).toLocaleString();


var verifyIncomeField = scope.allFormFields.filter(function(field) { return field.id === 'verifyincome' })[0];
if (!verifyIncomeField.value) {
    $('.toverifyemploymentandincomeforapplicant h3').text('To Verify Employment for Applicant');
}

console.log('Form Field Value Changed');

if (field.id == "showfullssn") {
    var element = document.getElementById('activiti-showfullssn');
    var decryptedSSNField;
    if (field.value === true) {
        decryptedSSNField = document.createElement('p');
        decryptedSSNField.id = 'decrypted-ssn';
        element.parentNode.appendChild(decryptedSSNField);
        var cipherField = scope.allFormFields.filter(function (el) {
            return el.id === "fullssn"
        });
        var hn = window.location.host;
        var url = location.protocol + '//' + hn + '/activiti-app/app/rest/corelogic/cldecrypt?cipher=%22' + window.encodeURIComponent(cipherField[0].value) + '%22';
        $.ajax
        ({
            method: "GET",
            url: url,
            dataType: 'json',
            cache : false,
            async: false,
            crossDomain: true,
            success: function (result) {
                decryptedSSNField.innerText = result.data;
            },
            fail: function (result) {
                console.log('failure' + result);
            }
        });
    } else {
        decryptedSSNField = document.getElementById('decrypted-ssn');
        element.parentNode.removeChild(decryptedSSNField);
    }
}

