javascriptEvents.javascriptLogic:
console.log('Form initiation');
var verifyIncomeField = scope.allFormFields.filter(function(field) { return field.id === 'verifyincome' })[0];
if (!verifyIncomeField.value) {
    $('.toverifyemploymentandincomeforapplicant h3').text('To Verify Employment for Applicant');
}

$('input[type=file]').each(function() { 
    $(this).attr('accept', '.pdf'); 
});

//Set a default date/time that is one day out
var fld = scope.allFormFields.filter(function(el) { return el.id === "callbackdate"})[0];
var today = new Date();
var month = ("00"+(today.getMonth()+1)).slice(-2);
var date = ("00"+(today.getDate()+1)).slice(-2);
var year = today.getFullYear();
var displayDt = month + "-" + date + "-" + year + " ";
var hrs = today.getHours();
if (hrs < 12) {
    if (hrs === 0) hrs = 12;
    displayDt += ("00"+hrs).slice(-2) + ":" + "00 AM";
} else {
    if (hrs != 12) hrs = hrs - 12;
    displayDt += ("00"+hrs).slice(-2) + ":" + "00 PM";
}

fld.value = displayDt;
var osf = scope.allFormFields.filter(function(el) { return el.id === "offset"})[0];
if (osf) {
    osf.value = -(new Date().getTimezoneOffset());
}

$('#activiti-employercontactname').each(function() { 
    $(this).attr('readonly', true); 
});

var countryName = scope.allFormFields.filter(function(field) { return field.id === 'country' })[0];
if(!!countryName && countryName.value === 'United States') {
    $('#activiti-phoneNumber').each(function() { 
        $(this).mask('(000) 000-0000', {reverse: false, placeholder: '(___) ___-____'});
    });
    $('#activiti-altPhoneNumber1').each(function() { 
        $(this).mask('(000) 000-0000', {reverse: false, placeholder: '(___) ___-____'});
    });
    $('#activiti-altPhoneNumber2').each(function() { 
        $(this).mask('(000) 000-0000', {reverse: false, placeholder: '(___) ___-____'});
    });
    $('#activiti-newemployercontactphone').each(function() { 
        $(this).mask('(000) 000-0000', {reverse: false, placeholder: '(___) ___-____'});
    });
}

$('#activiti-phoneNumber').attr('readonly', true);

$('#activiti-phoneExtension').each(function() { 
    $(this).attr('readonly', true); 
});

var employmentIsCurrent = scope.allFormFields.filter(function(field) { return field.id === 'employmentIsCurrent' })[0];
var employmentStatusField = scope.allFormFields.filter(function(field) { return field.id === 'employmentstatus' })[0];
if(!!employmentIsCurrent.value) {
    // do nothing
} else {
    employmentStatusField.options.splice(1, 1);
}

var dateCreated = scope.findFormFieldWithId(scope.allFormFields, 'datecreated');
dateCreated.value = new Date(dateCreated.value).toLocaleString();

if (field.id == "showfullssn") {
    var element = document.getElementById('activiti-showfullssn');
    var decryptedSSNField;
    if (field.value === true) {
        decryptedSSNField = document.createElement('p');
        decryptedSSNField.id = 'decrypted-ssn';
        element.parentNode.appendChild(decryptedSSNField);
        var cipherField = scope.allFormFields.filter(function (el) {
            return el.name === "SSN"
        });
        var hn = window.location.host;
        var url = location.protocol + '//' + hn + '/activiti-app/app/rest/corelogic/cldecrypt?cipher=%22' + window.encodeURIComponent(cipherField[0].value) + '%22';
        $.ajax
        ({
            method: "GET",
            url: url,
            dataType: 'json',
            cache : false,
            async: false,
            crossDomain: true,
            success: function (result) {
                decryptedSSNField.innerText = result.data;
            },
            fail: function (result) {
                console.log('failure' + result);
            }
        });
    } else {
        decryptedSSNField = document.getElementById('decrypted-ssn');
        element.parentNode.removeChild(decryptedSSNField);
    }
}

if (field.id == "remarks") {
   var value = field.value.replace(/\$/g, '');
   field.value = value;
}

if (field.id == "employmentIsCurrent") {
    var employmentStatusField = scope.allFormFields.filter(function(field) { return field.id === 'employmentstatus' })[0];
    if(!!field.value) {
         if (employmentStatusField.options.length < 3) {
            employmentStatusField.options.splice(1, 0, 
                {
                  id: "ACTIVE",
                  name: "Active"
                }
            );
        }
    } else {
         if(employmentStatusField.value.id === 'ACTIVE') {
            employmentStatusField.value = employmentStatusField.options[0];
        }
        if (employmentStatusField.options.length > 2) {
            employmentStatusField.options.splice(1, 1);
        }
    }
}

