properties.scripttext:
//Get information from specific verification
print("SINGLE VERIFICATION");
print(execution.getVariable("verification"));
var ver = JSON.parse(execution.getVariable("verification"));
var JDate = Java.type("java.util.Date");
var pattern = "mm/dd/yyyy";
var responseDatePattern = "yyyy-mm-dd";
var SimpleDateFormat = Java.type("java.text.SimpleDateFormat");
var simpleDateFormatInstance = new SimpleDateFormat(pattern);
var responseSimpleDateFormatInstance = new SimpleDateFormat(responseDatePattern);
execution.setVariable("voecompleted",false);
execution.setVariable("voeState","IN_PROGRESS");
execution.setVariable("executionId", execution.getProcessInstanceId());

var thirdparty = execution.getVariable("enumthirdparty");
var twn = execution.getVariable("enumtwn");
var selfemployed = execution.getVariable("enumselfemployed");
var isVerifyIncome = ver.includeVOI
execution.setVariable("vmcount",0);

var vm = ver.verificationMethod;

if(vm === '' || vm == twn)
{
    vm = execution.getVariable("enumphone");
}

//Applicant Info
var an = ver.applicantFirstName + " " + ver.applicantLastName;
execution.setVariable("applicantName",an);
var maskedSSN = "***-**-" + ver.SSNLastFour;
execution.setVariable("applicantSSN", maskedSSN);
execution.setVariable("fullSSN", ver.SSN);
execution.setVariable("applicantRefId",ver.applicantRefId)

//Fax
execution.setVariable("employerfaxnumber","");

//Third Party
execution.setVariable("thirdpartyname",(vm == thirdparty ? ver.thirdPartyName : ""));

//Process flow variables
execution.setVariable("waittime","PT0M");
execution.setVariable("contactIsValidated",true);
execution.setVariable("lastTask","Completed applicant consent review. ");
execution.setVariable("verifyIncome", isVerifyIncome);
execution.setVariable("employercontactname", ver.contactName);

var vu = {};
vu.productRequestRefId = execution.getVariable("requestId");
vu.loanNumber = ver.loanNumber;
vu.employerName = ver.employerName;
vu.employerId = "";
vu.employmentRefId = ver.employmentRefId;
vu.employeeId = ver.employeeId;
vu.verificationMethod = vm;

//Phone
vu.phoneNumber = ver.phone;
vu.phoneExtension = ver.phoneExt?ver.phoneExt:"" ;
vu.jobTitle = "";
vu.verifiedBy = "";
vu.startDate = new JDate();
vu.endDate = new JDate();
vu.employmentIsCurrent = ver.isEmploymentCurrent;

if(!!ver.startDate) {
    vu.employmentStartDate = simpleDateFormatInstance.format(responseSimpleDateFormatInstance.parse(ver.startDate));
}
if(!!ver.endDate) {
    vu.employmentEndDate = simpleDateFormatInstance.format(responseSimpleDateFormatInstance.parse(ver.endDate));
}
vu.whichPhoneNumber = "lenderProvided";

//Email
if (execution.getVariable("containsReverification") && vm==execution.getVariable("enumemail")) {
    vu.employeremail = ver.emailAddress;
    vu.employercontactname = ver.contactName;
}

//Fax
if (execution.getVariable("containsReverification") && vm==execution.getVariable("enumfax")) {
    vu.employerfaxnumber = ver.faxNumber;
    vu.faxattentionto = ver.faxAttentionTo;
}

//Self
vu.secretaryOfStateUrl = "http://www.secstates.com/";
vu.selfEmpStateReg = "";
vu.selfEmpThirdPartyFilingAgent = "";
vu.country = ver.country;
vu.countryName = ver.countryName;
vu.address1 = ver.address1;
vu.address2 = ver.address2;
vu.emplCity = ver.city;
vu.usState = ver.state;
if (vu.country != 'US'){
    vu.internationalState = ver.state;
}
vu.postalCode = ver.zip;
vu.residentAgentName = "";
vu.cpaName = ver.cpaName;
vu.cpaPhoneNumber = ver.cpaPhoneNumber;
vu.cpaPhoneExtension = ver.cpaPhoneExt?ver.cpaPhoneExt:"";
vu.cpaCompanyName = ver.cpaCompanyName?ver.cpaCompanyName:"";
vu.cpaVerificationDate = new JDate();

//Income
if(isVerifyIncome)
{
    vu.salaryKey = ver.salaryKey;
    vu.payFrequency = ""; // hourly, weekly, biweekly, monthly, annual
    vu.payBase = "";
    //vu.verificationMethod = execution.getVariable("enumphone");
    //execution.setVariable("isDefaultFax",true);
    //execution.setVariable("result", "updatemethod");
    //execution.setVariable("newverificationmethod", "Fax");
    // Other required fields for income here
}

vu.additionalGuidance = ver.additionalGuidance;

var objs = Object.keys(vu);
for (var i = 0; i < objs.length; i++) {
    execution.setVariable(objs[i],vu[objs[i]]);
}
execution.setVariable("verificationUpdate",JSON.stringify(vu));

execution.setVariable("voecompleted",true);

var JDate = Java.type("java.util.Date");
var vd = new JDate();
execution.setVariable("verificationdate",vd);
execution.setVariable("taskDueDate",vd);

if (execution.getVariable("result") === "notVerified") {
     var reason = execution.getVariable("utvreason");
    execution.setVariable("summary", "Unable to verify" + ((reason === null) ? "" : " - " + reason));
    var reason = (thirdPartyRejectResellers === false) ? "NOT_CONFIRMED" : "RESELLER_REJECTED";
    execution.setVariable("verificationOutcome", reason);
    print(execution.getVariable("summary"));
} else {
    var summary = "Verified"; // by email with employer //
    switch (execution.getVariable("verificationMethod")) {
        case execution.getVariable("enumphone"):
            summary += " by Phone with employer";
            break;
        case execution.getVariable("enumfax"):
            summary += " by Fax with employer";
            break;
        case execution.getVariable("enumemail"):
            summary += " by Email with employer";
            break;
        case execution.getVariable("enummail"):
            summary += " by Mail with employer";
            break;
        case execution.getVariable("enumthirdparty"):
            summary += " with " + execution.getVariable("thirdpartyname");
            break;
        case execution.getVariable("enummilitary"):
            var isCurrent = execution.getVariable("employmentIsCurrent");
            summary += " with " + (!!isCurrent ? "MyBiz" : "MyPay");
            break;
    }

    execution.setVariable("summary", summary);
    execution.setVariable("verificationOutcome","CONFIRMED_POSITIVE");
}

print("Updating values.....");
var international = execution.getVariable("country") != "US";

if(international) {
    execution.setVariable("employerfaxnumber",execution.getVariable("internationalFaxNumber"));
} else {
    execution.setVariable("employerfaxnumber",execution.getVariable("newemployerfaxnumber"));
}


if(execution.getVariable("newverificationmethod") === execution.getVariable("verificationMethod")) {
    execution.setVariable("disableCancel", "true");
} else {
    execution.setVariable("disableCancel", "false");
}

execution.setVariable("employercontactname",execution.getVariable("newemployercontact"));
if(execution.getVariable("newemployercontactphone") != null && execution.getVariable("newemployercontactphone") != '') {
    execution.setVariable("phoneNumber",execution.getVariable("newemployercontactphone"));
    execution.setVariable("phoneExtension",execution.getVariable("newemployerphoneextension"));
}

execution.setVariable("employeremail",execution.getVariable("newemployeremail"));
execution.setVariable("thirdpartyname",execution.getVariable("newthirdpartyname_LABEL"));
execution.setVariable("cpaCompanyName",execution.getVariable("newcpacompanyname"));
execution.setVariable("cpaName",execution.getVariable("newcpaname"));
execution.setVariable("cpaPhoneNumber",execution.getVariable("newcpaphonenumber"));
execution.setVariable("cpaPhoneExtension",execution.getVariable("newcpaphoneextension"));
execution.setVariable("faxattentionto",execution.getVariable("newfaxattentionto"));

execution.setVariable("isDefaultFax",false);
var nvm = execution.getVariable("newverificationmethod");
nvm = (nvm === null || nvm === '') ? execution.getVariable("") : nvm;
execution.setVariable("verificationMethod",nvm);
execution.setVariable("result",null);

var vm = execution.getVariable("verificationMethod");
var summ = "Will attempt to verify "

switch(vm) {
    case "3rd Party":
        summ += "via " + execution.getVariable("thirdpartyname");
        break;
    case "Self Employed":
        summ += "via CPA";
        break;
    default:
        summ += "via " + vm;
}

execution.setVariable("summary",summ);

var phone = execution.getVariable("enumphone");
switch (execution.getVariable("verificationMethod")) {
    case phone:
        execution.setVariable("utvreason",execution.getVariable("utvreasonph"));
        break;
    case execution.getVariable("enumthirdparty"):
        execution.setVariable("utvreason",execution.getVariable("utvreasontp"));
        break;
    case execution.getVariable("enumfax"):
        var utvreasonfax = execution.getVariable("utvreasonfa");
        if (!utvreasonfax) {
            utvreasonfax = execution.getVariable("utvreasonfa2");
        }
        execution.setVariable("utvreason", utvreasonfax);
        break;
    case execution.getVariable("enumemail"):
        var utvreasonemail = execution.getVariable("utvreasonem");
        if (!utvreasonemail) {
            utvreasonemail = execution.getVariable("utvreasonem2");
        }
        execution.setVariable("utvreason", utvreasonemail);
        break;
    case execution.getVariable("enummail"):
        var utvreasonmail = execution.getVariable("utvreasonma");
        if (!utvreasonmail) {
            utvreasonmail = execution.getVariable("utvreasonma2");
        }
        execution.setVariable("utvreason", utvreasonmail);
        break;
    case execution.getVariable("enumselfemployed"):
        execution.setVariable("utvreason",execution.getVariable("utvreasonse"));
        break;
}

var tn = 'Unable to verify';
if (!!execution.getVariable("utvreason")) {
 tn = "Unable to verify - " + execution.getVariable("utvreason") + ".";
}
print("Task Name: "+tn);

//If unable to validate, check for particular reasons that will require special handling
execution.setVariable("thirdPartyRejectResellers",false);
var requiresID;

if (execution.getVariable("verificationMethod") == execution.getVariable("enumthirdparty")) {
    var rejectsResellers = (execution.getVariable("utvreasontp") == "Does not allow resellers");
    var requiresSK = (execution.getVariable("utvreasontp") == "Requires Salary Key");
    var requiresEID = (execution.getVariable("utvreasontp") == "Requires Employer ID");

    requiresID = requiresSK || requiresEID || rejectsResellers;
    print('requiresID = requiresSK || requiresEID || rejectsResellers ===============>' + requiresID);
    if (requiresID) {
        execution.setVariable("thirdPartyRequiresSalaryKey",requiresSK);
        execution.setVariable("thirdPartyRequiresEmployerID",requiresEID);
        execution.setVariable("newverificationmethod",execution.getVariable("enumthirdparty"));
        execution.setVariable("newthirdpartyname_LABEL",execution.getVariable("thirdpartyname"));
    }
    execution.setVariable("thirdPartyRejectResellers",rejectsResellers);
}

execution.setVariable("taskNames",tn);

//If any method reaches a dead end, then switch to phone for a final try
if(!requiresID)
{
    print('If any method reaches a dead end, then switch to phone for a final try');
    execution.setVariable("newverificationmethod",phone);
    tn += " Calling employer";
}

//set values for enum and display verification methods
execution.setVariable("enumtwn", "TWN");
execution.setVariable("enumthirdparty", "3rd Party");
execution.setVariable("enumemail", "Email");
execution.setVariable("enumphone", "Phone");
execution.setVariable("enumfax", "Fax");
execution.setVariable("enumselfemployed", "Self Employed");
execution.setVariable("enummilitary", "Military");
execution.setVariable("enummail", "Mail");

var exec = execution;
var res = exec.getVariable("result");
print("The followup mechanism is ...  " + res)

//Generate the requiredd java types
var JTZO = Java.type("java.time.ZoneOffset");
var JZID = Java.type("java.time.ZoneId");
var JZDT = Java.type("java.time.ZonedDateTime");
var JDUR = Java.type("java.time.Duration");

//Set the starting values
var rightNow = JZDT.now();
//var wait = 24*60*60; //Set a default wait time to 24 hours in seconds
var wait = 0;
var myTZ = exec.getVariable("callbacktimezone");
var myCB = new Date(exec.getVariable("callbackdate"));
var targetTime = "";
var myDate;

if(res == "callback") {
    //Calculate the difference between the user's time zone and the target time zone
    var targetTZOffset = (JTZO.from(JZDT.now(JZID.of(myTZ))).getTotalSeconds())/60;
    var systemTZOffset = (execution.getVariable("offset"));
    var TZdiff = (systemTZOffset - targetTZOffset)/60;

    //Update the UTC time to reflect the difference
    myCB.setHours(myCB.getHours() + TZdiff);
    targetTime = myCB.toISOString();
    mydate = JZDT.parse(targetTime);
    wait = Math.abs(JDUR.between(mydate, rightNow).getSeconds());
    exec.setVariable("progressMessageSummary", "Callback scheduled");
} else if (res == "vm") {
    myCB = (new Date());
    myCB.setDate(myCB.getDate()+1);
    targetTime = myCB.toISOString();
    exec.setVariable("progressMessageSummary", "Left a voice mail");
    //Check if they have left enough VM
    // var newVmcount = exec.getVariable("vmcount") + 1;
    // if (newVmcount > 4) {
    //     exec.setVariable("result","notVerified");
    //     exec.setVariable("utvreasonph","Employer Unresponsive");
    // } else {
    //     exec.setVariable("vmcount",newVmcount);
    // }
}

exec.setVariable("callbackdate","");
exec.setVariable("callbackwait","PT"+wait+"S");
print("The target date/time is " + targetTime);
print("Generating a delay of " + wait + " seconds");
